INSERT INTO `cms_news` VALUES (21,3,'谷歌公开其神秘操作系统 或将取代安卓','谷歌公开其神秘操作系统','#5674ed','/upload/2017/05/11/59133a1ebfcf5.jpg','谷歌','谷歌公开其神秘操作系统 或将取代安卓','',40,1,'1','admin',1457854896,0,456484),(22,8,'PHP开发的发展现状和前景','PHP开发的发展现状和前景','#ed568b','/upload/2017/05/10/59132ae91c95b.jpg','PHP开发的发展现状和前景','PHP开发的发展现状和前景','',30,1,'0','admin',1457855362,0,131243),(23,8,'适用于PHP初学者的学习线路和建议','适用于PHP初学者的学习线路和建议','#ed568b','/upload/2017/05/10/591329aba5120.jpg','适用于PHP初学者的学习线路和建议','适用于PHP初学者的学习线路和建议','',10,1,'0','admin',1457855680,0,453216),(24,12,'苹果iOS 11愿望大清单：增加暗黑模式如何？','苹果iOS 11愿望大清单','#5674ed','/upload/2017/05/11/591339b8341c6.jpg','暗黑模式','苹果iOS 11愿望大清单：增加暗黑模式如何？','',20,1,'0','admin',1457856460,0,456133);

